mapper.select:
select date_format(mytime,'%Y-%m-%d') as time,count(*) as num from
(select * from time_info where date_format(mytime,'%H:%i:%S')>=#{time1}and #{time2}>=date_format(mytime,'%H:%i:%S') order by mytime ASC) as temp where mac_address in (select mac_address from address_info
where city=#{city}
and county=#{county}
and street=#{street})
and (mytime between #{date1}
and #{date2})
group by time
order by time

select date_format(mytime,'%Y-%m-%d') as time,count(*) as num from
(select * from time_info where date_format(mytime,'%H:%i:%S')>=#{time1}and #{time2}>=date_format(mytime,'%H:%i:%S') order by mytime ASC) as temp where mac_address in (select mac_address from address_info
where city=#{city} and county=#{county}
and street=#{street}
and (specific_address like #{specific_address}"%"))
and (mytime between #{date1}
and #{date2})
group by time
order by time

